mapper.update:
UPDATE user
   SET name = #{name}
      ,email = #{email}
      ,profile = #{profile}
      ,updated_at = now()
WHERE username = #{username}

mapper.select:
SELECT user_id
  FROM user
 WHERE phone_number = #{phoneNumber}
   AND username = #{userName}

SELECT password
  FROM user
 WHERE username = #{userName}
   AND birth = #{birth}
   AND user_id = #{userId}
